SELECT wtm.module_id , wtm.module_pid , wtm.module_type , wtm.module_name , wtm.module_item_line_num , wtm.module_item_column_num , wtm.module_tmpl , wtm.module_style_type , wtm.show_titile , wtm.extend_url , wtm.module_style_height, wtm.module_style_width , wtm.module_style_show , wtm.page_size , wtm.page_show , mrc.content_data_type , mrc.data_target_table , mrc.data_column_id , wtm.site_id FROM wms_template_module AS wtm JOIN wms_template_module_rel_content AS mrc ON wtm.module_id = mrc.module_id WHERE wtm.data_status = 0 AND mrc.rel_status = 0
AND wtm.module_id = #{moduleId}
AND wtm.module_pid = #{modulePid}
AND wtm.site_id = #{siteId}
AND wtm.module_id IN
#{item}
