mapper.select:
SELECT tc.template_id as templateId , tc.template_code as templateCode, tc.template_name as templateName, tc.industry_type_id as industryTypeId, tc.color_id as colorId , tc.platform_id as platformId , tc.template_banner_id as templateBannerId, tc.template_logo_id as templateLogoId, tc.template_footer_id as templateFooterId, tc.template_thumbnail_url as templateThumbnailUrl , tc.is_effect as isEffect , tc.is_recommended as isRecommended , tc.template_size as templateSize , tc.template_standard_width as templateStandardWidth , tc.template_standard_height as templateStandardHeight , tc.operator_id as operatorId , tc.operator_name as operatorName , tc.is_contains_vedio as isContainsVedio , (select distinct urt.user_id from wms_user_rel_template as urt where urt.user_id=#{userId} and urt.template_id=tc.template_id) isCollect , tc.template_view_url as templateViewUrl FROM wms_template as tc WHERE tc.data_status = 0
and tc.template_id = #{templateId}
and tc.industry_type_id = #{industryTypeId}
and tc.color_id = #{colorId}
and tc.is_recommended = #{isRecommended}
and ifnull(tc.is_contains_vedio,0) = #{isContainsVedio}
order by tc.update_time desc

SELECT tc.template_id as templateId , tc.template_code as templateCode, tc.template_name as templateName, tc.industry_type_id as industryTypeId, tc.color_id as colorId , tc.platform_id as platformId , tc.template_banner_id as templateBannerId, tc.template_logo_id as templateLogoId, tc.template_footer_id as templateFooterId, tc.template_thumbnail_url as templateThumbnailUrl , tc.is_effect as isEffect , tc.is_recommended as isRecommended , tc.template_size as templateSize , tc.template_standard_width as templateStandardWidth , tc.template_standard_height as templateStandardHeight , tc.operator_id as operatorId , tc.operator_name as operatorName , tc.is_contains_vedio as isContainsVedio , #{userId} as isCollect , tc.template_view_url as templateViewUrl FROM wms_template as tc LEFT JOIN wms_user_rel_template as urt ON urt.template_id = tc.template_id WHERE tc.data_status = 0 and urt.user_id= #{userId} and urt.user_rel_template_id is not null
and tc.template_id = #{templateId}
and tc.industry_type_id = #{industryTypeId}
and tc.color_id = #{colorId}
and tc.is_recommended = #{isRecommended}
and ifnull(tc.is_contains_vedio,0) = #{isContainsVedio}
order by tc.update_time desc
