SELECT DISTINCT wa.article_id as articleId, wa.article_title as articleTitle, wa.article_author as articleAuthor, wa.article_url as articleUrl, wa.article_size as articleSize, wa.article_brief as articleBrief, wa.article_content as articleContent, wa.article_attachment_img AS articleAttachmentImg, wa.article_word_num as articleWordNum, wa.operator_id as operatorId, wa.update_time as updateTime, wa.create_time as createTime, wa.data_status as dataStatus, wa.operator_name as operatorName, wat.article_type_id as articleTypeId, wa.article_top AS articleTop, wat.article_type_name as articleTypeName FROM wms_article AS wa INNER JOIN wms_article_rel_type art ON wa.article_id = art.article_id LEFT JOIN wms_article_type as wat ON art.article_type_id = wat.article_type_id WHERE wa.data_status = 0
AND art.article_type_id = #{articleTypeId}
AND wa.article_review = #{articleReview}
AND art.article_type_id in
#{item}
AND wa.article_id = #{articleId}
AND wa.article_title like '%${articleTitle}%'
ORDER BY wa.article_top DESC , wa.update_time DESC

SELECT DISTINCT wa.article_id AS articleId, wa.article_title AS articleTitle, wa.article_author AS articleAuthor, wa.article_url AS articleUrl, wa.article_size AS articleSize, wa.article_ntro AS articleNtro, wa.article_brief AS articleBrief, wa.article_content AS articleContent, wa.article_attachment_img AS articleAttachmentImg, wa.article_word_num AS articleWordNum, wa.operator_id AS operatorId, wa.operator_name AS operatorName, wa.update_time AS updateTime, wa.create_time AS createTime, wa.data_status AS dataStatus, wa.article_top AS articleTop, wa.article_source AS articleSource, wa.article_review AS articleReview FROM wms_article AS wa LEFT JOIN wms_article_rel_type AS wart ON wa.article_id = wart.article_id WHERE wa.data_status = 0
AND wa.article_title LIKE '%${articleTitle}%'
AND wa.article_review = #{articleReview}
AND wart.article_type_id IN
#{item}
AND wa.article_id IN
#{item}
ORDER BY wa.article_top DESC , wa.update_time DESC
,wa.article_review ASC
,wa.article_review DESC
limit #{limitNum}
