mapper.select:
SELECT DISTINCT tc.columns_id as columnsId , tc.columns_name as columnsName, ct.columns_type_name as columnsTypeName, tc.columns_pid as columnsPid, tc.open_type as openType , tc.custom_url as customUrl , tc.data_status as dataStatus, trc.template_rel_columns_id as templateRelColumnsId, trc.sort_num as sortNum, tc.columns_icon as columnsIcon , tc.columns_type_id as columnsTypeId , trc.columns_hide as columnsHide , tc.columns_icon_active as columnsIconActive FROM wms_template_columns as tc INNER JOIN wms_template_rel_columns as trc ON tc.columns_id = trc.columns_id INNER JOIN wms_columns_type as ct ON tc.columns_type_id = ct.columns_type_id WHERE tc.data_status = 0 AND ct.data_status = 0 AND trc.rel_status = 0
AND trc.template_id = #{templateId}
AND trc.columns_id = #{columnsId}
AND ifnull(trc.rel_status,0) = #{relStatus}
AND ifnull(trc.columns_hide,0) = #{columnsHide}
order by trc.sort_num

SELECT DISTINCT tc.columns_id as columnsId , tc.columns_name as columnsName, ct.columns_type_name as columnsTypeName, tc.columns_pid as columnsPid, tc.open_type as openType , tc.custom_url as customUrl , tc.data_status as dataStatus, tfrc.template_footer_rel_columns_id as templateFooterRelColumnsId , tfrc.sort_num as sortNum , tfrc.columns_hide as columnsHide , tc.columns_type_id as columnsTypeId , ct.columns_type_name as columnsTypeName FROM wms_template_columns as tc INNER JOIN wms_template_footer_rel_columns as tfrc ON tc.columns_id = tfrc.columns_id INNER JOIN wms_columns_type as ct ON tc.columns_type_id = ct.columns_type_id WHERE tc.data_status = 0 AND ct.data_status = 0 AND tfrc.rel_status = 0
AND tfrc.template_footer_id = #{templateFooterId}
AND ifnull(tfrc.columns_hide,0) = #{columnsHide}
AND ifnull(tfrc.rel_status,0) = #{relStatus}
order by tfrc.sort_num
